'use strict';

const Transformer = require('./transformer');
const patternTransformer = require('./pattern-transformer');

/**
 * Apply transformations to an configuration container
 */
class ContainerTransformer {
  /**
   * @param {Container} container
   */
  constructor(container) {
    this._container = container;
    this._transformers = [];
  }

  /**
   * @param {string} path
   *
   * @returns {ContainerTransformer}
   */
  addPattern(path) {
    return this.add({ path, transformer: patternTransformer });
  }

  /**
   * @param {string} path
   * @param {function} transformer
   *
   * @returns {Transformer}
   */
  create(path, transformer) {
    return new Transformer(path, transformer);
  }

  /**
   * @param {Transformer} transformerInstance
   *
   * @returns {ContainerTransformer}
   */
  add(transformerInstance) {
    if (!(transformerInstance instanceof Transformer)) {
      const { path, transformer } = transformerInstance;

      transformerInstance = this.create(path, transformer);
    }

    this._transformers.push(transformerInstance);

    return this;
  }

  /**
   * @returns {Transformer[]}
   */
  get transformers() {
    return this._transformers;
  }

  /**
   * @returns {Promise}
   */
  transform() {
    return Promise.all(this.transformers.map(transformer => {
      if (this.container.has(transformer.path)) {
        return transformer.transform(this.container.get(transformer.path))
          .then(value => {
            this.container.set(transformer.path, value);

            return Promise.resolve();
          });
      }

      return Promise.resolve();
    })).then(() => Promise.resolve(this.container));
  }

  /**
   * @returns {Container}
   */
  get container() {
    return this._container;
  }
}

module.exports = ContainerTransformer;

'use strict';

const Module = require('module');
const SandboxedModule = require('sandboxed-module');

/**
 * Overwrites original Module to apply compilers
 */
class ModuleCompile {
  /**
   * @param {*} compilers
   */
  constructor(...compilers) {
    this._compilers = compilers;
    this._originalCompile = Module.prototype._compile;
  }

  /**
   * @returns {Function}
   */
  get originalCompile() {
    return this._originalCompile;
  }

  /**
   * @returns {Function[]}
   */
  get compilers() {
    return this._compilers
      .filter(c => c && c instanceof Function)
      .concat([ this.originalCompile ]);
  }

  /**
   * @param {Function} compiler
   *
   * @returns {ModuleCompile}
   */
  add(compiler) {
    this._compilers.push(compiler);

    return this;
  }

  /**
   * @param {Function} compiler
   *
   * @returns {ModuleCompile}
   */
  remove(compiler) {
    delete this._compilers[this._compilers.indexOf(compiler)];

    return this;
  }

  /**
   * @param {boolean} clearCache
   * 
   * @returns {ModuleCompile}
   */
  register(clearCache = true) {
    const self = this;

    if (clearCache) {
      ModuleCompile.clearCache();
    }

    Module.prototype._compile = function(content, filename) {
      return self.compilers
        .reduce((content, compiler) => {
          return compiler.call(this, content, filename);
        }, content);
    };

    return this;
  }

  /**
   * @param {boolean} clearCache
   * 
   * @returns {ModuleCompile}
   */
  restore(clearCache = true) {
    Module.prototype._compile = this.originalCompile;

    if (clearCache) {
      ModuleCompile.clearCache();
    }

    return this;
  }

  /**
   * @param {string} moduleId
   * @param {*} options
   *
   * @returns {*}
   */
  static require(moduleId, options = {}) {
    return SandboxedModule.require(moduleId, options);
  }

  /**
   * @param {string} cacheKey
   * 
   * @returns {ModuleCompile}
   */
  static clearCache(cacheKey = null) {
    if (cacheKey) {
      delete require.cache[cacheKey];

      return this;
    }

    Object.keys(require.cache).map(cacheKey => {
      delete require.cache[cacheKey];
    });

    return this;
  }
}

module.exports = ModuleCompile;

'use strict';

/**
 * @param {string} value
 *
 * @returns {RegExp}
 */
function toRegexp(value) {
  const matches = value.match(/^\/(.+)\/([gmiyu])*$/);

  const [ , regex, modifiers ] = matches;

  return new RegExp(regex, modifiers);
}

/**
 * @param {string} value
 *
 * @returns {boolean}
 */
function isRegexp(value) {
  return /\/.+\/[a-z]*/i.test(value);
}

/**
 * @param {*} value
 *
 * @returns {Promise}
 */
module.exports = (value => {  
  return Promise.resolve((Array.isArray(value) ? value : [ value ])
    .filter(pattern => !!((pattern || '').trim()))
    .map(pattern => {
      if (isRegexp(pattern)) {
        return toRegexp(pattern);
      }

      return pattern;
    }));
});

'use strict';

/**
 * Implements Promise.all() but sequential execution
 */
class SequentialPromise {
  /**
   * @param {function[]} promises
   * @param {*} value
   * 
   * @returns {Promise}
   */
  static all(promises, value = null) {
    const promisesCloned = [].concat(promises);

    return this._sequential(promisesCloned, value);
  }

  /**
   * @param {function[]} promises
   * @param {*} result
   * 
   * @returns {Promise}
   *
   * @private
   */
  static _sequential(promises, result) {
    if (promises.length <= 0) {
      return Promise.resolve(result);
    }

    return promises.shift()(result)
      .then(result => SequentialPromise._sequential(promises, result));
  }
}

module.exports = SequentialPromise;

'use strict';

const ora = require('ora');

/**
 * Terminal spinner implementation
 */
class Spinner {
  /**
   * @param {string} msg
   */
  constructor(msg) {
    this._main = msg;
    this._then = msg;
    this._catch = msg;
    this._spinner = null;
  }

  /**
   * @param {string} text
   *
   * @returns {ora}
   */
  prepend(text) {
    this.spinner.text = `${ text } ${ this._main }`;

    return this;
  }

  /**
   * @param {string} text
   *
   * @returns {ora}
   */
  append(text) {
    this.spinner.text = `${ this._main } ${ text }`;

    return this;
  }

  /**
   * @returns {ora}
   */
  get spinner() {
    if (!this._spinner) {
      this._spinner = ora(this._main);
    }

    return this._spinner;
  }

  /**
   * @param {Promise} promiseToWrap
   *
   * @returns {Promise}
   */
  promise(promiseToWrap) {
    this.spinner.start();

    return promiseToWrap
      .then(result => {
        this.spinner.succeed(this.thenText);

        return Promise.resolve(result);
      })
      .catch(error => {
        this.spinner.fail(this.catchText);

        return Promise.reject(error);
      });
  }

  /**
   * @param {string} msg
   *
   * @returns {Spinner}
   */
  then(msg) {
    this._then = msg;

    return this;
  }

  /**
   * @param {string} msg
   *
   * @returns {Spinner}
   */
  catch(msg) {
    this._catch = msg;

    return this;
  }

  /**
   * @returns {string}
   */
  get catchText() {
    return this._catch + '\n';
  }

  /**
   * @returns {string}
   */
  get thenText() {
    return this._then + '\n';
  }

  /**
   * @returns {string}
   */
  get mainText() {
    return this._main + '\n';
  }
}

module.exports = Spinner;

