'use strict';

const events = require('../events');

/**
 * Abstract component
 */
class AbstractComponent {
  constructor() {
    this._logger = console;
    this._active = false;
    this._processing = 0;
  }

  /**
   * @returns {number}
   */
  get processing() {
    return this._processing;
  }

  /**
   * @param {number} interval
   *
   * @returns {Promise}
   */
  waitProcessing(interval = 200) {
    return new Promise(resolve => {
      if (!this.isProcessing) {
        return process.nextTick(() => resolve());
      }

      const id = setInterval(() => {
        if (!this.isProcessing) {
          clearInterval(id);
          process.nextTick(() => resolve());
        }
      }, interval);
    });
  }

  /**
   * @returns {AbstractComponent}
   */
  removeProcessing() {
    this._processing--;

    return this;
  }

  /**
   * @returns {AbstractComponent}
   */
  addProcessing() {
    this._processing++;

    return this;
  }

  /**
   * @returns {boolean}
   */
  get isProcessing() {
    return this.processing > 0;
  }

  /**
   * @returns {*}
   */
  get logger() {
    return this._logger;
  }

  /**
   * @param {*} logger
   *
   * @returns {AbstractComponent}
   */
  setLogger(logger) {
    this._logger = logger;

    return this;
  }

  /**
   * @param {boolean} state
   *
   * @returns {AbstractComponent}
   */
  setActive(state) {
    this._active = !!state;

    return this;
  }

  /**
   * @returns {boolean}
   */
  get isActive() {
    return !!this._active;
  }

  /**
   * @throws {Error}
   */
  get name() {
    throw new Error(`${ this.constructor.name }.name not implemented!`);
  }

  /**
   * @returns {*}
   */
  get events() {
    return events;
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   */
  init(emitter) {
    return Promise.resolve();
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   */
  run(emitter) {
    return Promise.resolve();
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   */
  teardown(emitter) {
    return Promise.resolve();
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   */
  subscribe(emitter) {
    return Promise.resolve();
  }

  /**
   * @returns {Promise}
   */
  ready() {
    return Promise.resolve();
  }
}

module.exports = AbstractComponent;

'use strict';

const jag = require('jag');
const path = require('path');
const fse = require('fs-extra');
const pify = require('pify');
const EventEmitter = require('events');
const events = require('./events');
const fs = require('fs');

/**
 * Abstract cache driver
 */
class AbstractDriver extends EventEmitter {
  /**
   * @param {string} cacheDir
   */
  constructor(cacheDir) {
    super();

    this._cacheDir = cacheDir;
    this._progressType = null;
  }

  /**
   * @returns {string}
   */
  get cacheDir() {
    return this._cacheDir;
  }

  /**
   * @returns {Promise}
   */
  upload() {
    return this._switchProgressType('upload')
      ._removePackageFile()
      .then(() => this._pack())
      .then(() => this._upload())
      .then(() => this._resetOperation());
  }

  /**
   * @returns {Promise}
   */
  download() {
    return this._switchProgressType('download')
      ._removePackageFile()
      .then(() => this._download())
      .then(() => this._unpack())
      .then(() => this._resetOperation());
  }

  /**
   * @param {string} type
   *
   * @returns {AbstractDriver}
   *
   * @private
   */
  _switchProgressType(type) {
    this._progressType = type;

    return this;
  }

  /**
   * @param {number} total
   * @param {number} amount
   *
   * @returns {AbstractDriver}
   *
   * @private
   */
  _progress(total, amount) {
    const payload = {
      amount, total, remaining: total - amount,
    };

    this.emit(events.cache.progress, payload);
    this.emit(events.cache[this._progressType].progress, payload);

    return this;
  }

  /**
   * @returns {Promise}
   * 
   * @private
   */
  _resetOperation() {
    this._progressType = null;

    return this._removePackageFile();
  }

  /**
   * @returns {Promise}
   * 
   * @private
   */
  _removePackageFile() {
    return fse.remove(this._packagePath);
  }

  /**
   * @returns {string}
   *
   * @private
   */
  get _packagePath() {
    return path.join(
      path.dirname(this.cacheDir),
      (path.basename(this.cacheDir) || AbstractDriver.DEFAULT_NS) + '.tar.gz'
    );
  }

  /**
   * @returns {Promise}
   *
   * @private
   */
  _pack() {
    const packagePath = this._packagePath;

    return fse.pathExists(this.cacheDir)
      .then(result => {
        if (!result) {
          return Promise.resolve();
        }

        return pify(jag.pack)(this.cacheDir, packagePath)
          .then(() => {

            // @todo remove when fixed
            // @see https://github.com/coderaiser/node-jag/blob/master/lib/jag.js#L53
            const tmpPackagePath = path.join(
              path.dirname(packagePath),
              path.basename(packagePath, '.tar.gz')
            ) + '.tar.tar.gz';

            return fse.move(
              tmpPackagePath, 
              packagePath, 
              { overwrite: true }
            );
          });
      });
  }

  /**
   * @returns {Promise}
   *
   * @private
   */
  _unpack() {
    return fse.pathExists(this._packagePath)
      .then(result => {
        if (!result) {
          return Promise.resolve();
        }

        return this._packageSize
          .then(packageSize => {            
            if (packageSize <= 0) {
              return Promise.resolve();
            }

            return fse.ensureDir(this.cacheDir)
              .then(() => {
                return pify(jag.unpack)(this._packagePath, this.cacheDir);
              });
          });
      });
  }

  /**
   * @returns {Promise}
   *
   * @private
   */
  get _packageSize() {
    return pify(fs.stat)(this._packagePath)
      .then(stat => Promise.resolve(stat.size));
  }

  /**
   * @returns {Promise}
   *
   * @private
   */
  _upload() {
    return Promise.reject(new Error(
      `${ this.constructor.name }._upload() not implemented!`
    ));
  }

  /**
   * @throws {Error}
   */
  get name() {
    throw new Error(
      `${ this.constructor.name }.name not implemented!`
    );
  }

  /**
   * @returns {Promise}
   *
   * @private
   */
  _download() {
    return Promise.reject(new Error(
      `${ this.constructor.name }._download() not implemented!`
    ));
  }

  /**
   * @returns {string}
   */
  static get DEFAULT_NS() {
    return 'recink_package';
  }
}

module.exports = AbstractDriver;

'use strict';

const dot = require('dot-object');

/**
 * Cache component events
 */
const events = {};

events.$ = [
  'cache.progress',
  'cache.upload.progress',
  'cache.download.progress',
].map(eventPath => {
  dot.str(eventPath, eventPath, events);

  return eventPath;
});

module.exports = events;

'use strict';

const AbstractDriver = require('./abstract-driver');

/**
 * Cache factory
 */
class Factory {  
  /**
   * @param {string} name
   * @param {*} args
   *
   * @returns {AbstractDriver}
   */
  static create(name, ...args) {
    const DriverImplementation = require(`./${ name }-driver`);
    const driver = new DriverImplementation(...args);

    if (!(driver instanceof AbstractDriver)) {
      throw new Error(`${ name } driver should be an implementation of AbstractDriver`);
    }

    return driver;
  }

  /**
   * @param {*} args
   *
   * @returns {VoidDriver}
   */
  static s3(...args) {
    return this.create('s3', ...args);
  }

  /**
   * @param {*} args
   *
   * @returns {VoidDriver}
   */
  static void(...args) {
    return this.create('void', ...args);
  }
}

module.exports = Factory;

'use strict';

const fs = require('fs');
const path = require('path');
const pify = require('pify');
const md5File = require('md5-file');
const progress = require('progress-stream');
const AbstractDriver = require('./abstract-driver');
const AwsCredentials = require('../helper/aws-credentials');

/**
 * AWS S3 cache driver
 */
class S3Driver extends AbstractDriver {
  /**
   * @param {string} cacheDir
   * @param {string} path
   * @param {*} options
   * @param {boolean} includeNodeVersion
   */
  constructor(cacheDir, path, options, includeNodeVersion = true) {
    super(cacheDir);

    this._path = path;
    this._options = options;
    this._includeNodeVersion = includeNodeVersion;
    this._awsCredentials = new AwsCredentials(this.options);
    this._client = false;
  }

  /**
   * @returns {string}
   */
  get name() {
    return 's3';
  }

  /**
   * @returns {boolean}
   */
  get includeNodeVersion() {
    return this._includeNodeVersion;
  }

  /**
   * @returns {string}
   */
  get path() {
    return this._path;
  }

  /**
   * Get AWS.S3 client promise
   * @return {Promise}
   */
  get client() {
    if (this._client) {
      return Promise.resolve(this._client);
    }

    return this._awsCredentials.getConfig().then(AWS => Promise.resolve(new AWS.S3()));
  }

  /**
   * @returns {*}
   */
  get options() {
    return this._options;
  }

  /**
   * @returns {Promise}
   * @private
   */
  _upload() {
    return this._packageSize.then(ContentLength => {
      if (ContentLength <= 0) {
        return Promise.resolve();
      }

      return this._hasChanged.then(hasChanged => {
        return new Promise((resolve, reject) => {
          if (!hasChanged) {
            return resolve();
          }

          const { Bucket, Key } = this._s3Location(this.path);
          const packageStream = fs.createReadStream(this._packagePath);

          packageStream.on('error', error => reject(error));

          const Body = this._track(packageStream, ContentLength);

          this.client
            .then(S3 => S3.upload({ Bucket, Key, Body }).promise())
            .then(() => resolve())
            .catch(error => reject(error));
        });
      });
    });
  }

  /**
   * @returns {Promise}
   * @private
   */
  get _hasChanged() {
    return pify(md5File)(this._packagePath).then(packageHash => {
      const { Bucket, Key } = this._s3Location(this.path);

      return this.client
        .then(S3 => S3.headObject({ Bucket, Key }).promise())
        .then(data => {
          const remoteHash = data.ETag.replace(/"/g, '');

          return Promise.resolve(packageHash !== remoteHash);
        })
        .catch(error => {
          if (this._isMissingObject(error)) {
            return Promise.resolve(true);
          }

          return Promise.reject(error);
        });
    });
  }

  /**
   * @returns {Promise}
   *
   * @private
   */
  _download() {
    return new Promise((resolve, reject) => {
      this.client.then(S3 => {
        const { Bucket, Key } = this._s3Location(this.path);
        const packageStream = fs.createWriteStream(this._packagePath);
        const remoteStream = S3.getObject({ Bucket, Key }).createReadStream();

        remoteStream.on('end', () => resolve());
        remoteStream.on('error', error => {
          if (this._isMissingObject(error)) {
            return resolve();
          }

          reject(error);
        });
        packageStream.on('error', error => reject(error));

        this._track(remoteStream).pipe(packageStream);
      });
    });
  }

  /**
   * @param {*} error
   *
   * @returns {boolean}
   * 
   * @private
   */
  _isMissingObject(error) {
    return [ 'NoSuchKey', 'NotFound' ].indexOf(error.code) !== -1;
  }

  /**
   * @param {ReadableStream} stream
   * @param {number} length
   * @param {number} time
   *
   * @returns {ReadableStream}
   * 
   * @private
   */
  _track(stream, length = null, time = 50) {
    const tracker = progress({ length, time, });

    tracker.on('progress', progress => {
      this._progress(progress.length, progress.transferred);
    });

    return stream.pipe(tracker);
  }

  /**
   * @param {string} s3Path
   *
   * @returns {*}
   *
   * @private
   */
  _s3Location(s3Path) {
    const matches = s3Path.match(
      /^(?:s3:\/\/|\/)?([^\/]+)(?:\/(.*))?$/i
    );

    if (matches.length === 2) {
      matches.push('');
    }

    const [ , Bucket, keyPrefix ] = matches;

    const Key = path.join(
      keyPrefix || '',
      this.includeNodeVersion ? process.version : '',
      path.basename(this._packagePath)
    ).replace(/\\+/g, '/'); // ensure path delimiter set to slash

    return { Bucket, Key };
  }
}

module.exports = S3Driver;

'use strict';

const AbstractDriver = require('./abstract-driver');

/**
 * Void cache driver
 */
class VoidDriver extends AbstractDriver {
  /**
   * @returns {string}
   */
  get name() {
    return 'void';
  }

  /**
   * @returns {Promise}
   *
   * @private
   */
  _upload() {
    return Promise.resolve();
  }

  /**
   * @returns {Promise}
   *
   * @private
   */
  _download() {
    return Promise.resolve();
  }
}

module.exports = VoidDriver;

'use strict';

const npmEvents = require('./npm/events');
const CacheFactory = require('./cache/factory');
const DependencyBasedComponent = require('./dependency-based-component');
const Spinner = require('./helper/spinner');
const cacheEvents = require('./cache/events');
const prettyBytes = require('pretty-bytes');
const SequentialPromise = require('./helper/sequential-promise');

/**
 * Cache component
 */
class CacheComponent extends DependencyBasedComponent {
  /**
   * @param {*} args
   */
  constructor(...args) {
    super(...args);

    this._caches = [];
  }

  /**
   * @returns {string}
   */
  get name() {
    return 'cache';
  }

  /**
   * @returns {string[]}
   */
  get dependencies() {
    return [];
  }

  /**
   * @returns {AbstractDriver[]}
   */
  get caches() {
    return this._caches;
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   */
  init(emitter) {
    return Promise.all([
      this._initCaches(emitter),
      this._initNpmCache(emitter)
    ]);
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   */
  teardown(emitter) {
    if (this.caches.length <= 0) {
      return Promise.resolve();
    }

    return SequentialPromise.all(this.caches.map(cache => {
      return () => this._uploadCache(cache);
    }));
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   * 
   * @private
   */
  _initCaches(emitter) {
    const cachePaths = this.container.get('paths', []);

    if (cachePaths.length <= 0) {
      return Promise.resolve();
    }

    return SequentialPromise.all(cachePaths.map(cacheDir => {
      return () => {
        const cache = this._createCache(cacheDir);

        this.caches.push(cache);

        return this._downloadCache(cache);
      };
    }));
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   * 
   * @private
   */
  _initNpmCache(emitter) {
    const enabled = this.container.get('npm', true);

    if (!enabled || !emitter.component('npm')) {
      return Promise.resolve();
    }

    emitter.onBlocking(npmEvents.npm.cache.init, cacheDir => {
      const npmCache = this._createCache(cacheDir);

      this.caches.push(npmCache);

      return this._downloadCache(npmCache);
    });

    return Promise.resolve();
  }

  /**
   * @param {string} cacheDir
   *
   * @returns {AbstractDriver}
   *
   * @private
   */
  _createCache(cacheDir) {
    return CacheFactory.create(
      this.container.get('driver'),
      cacheDir,
      ...this.container.get('options', [])
    );
  }

  /**
   * @param {AbstractDriver} cache 
   * 
   * @returns {Promise}
   * 
   * @private
   */
  _downloadCache(cache) {
    const spinner = new Spinner(`Downloading caches from #${ cache.name }`);

    return spinner.then(
      `Caches downloaded to ${ cache.cacheDir }`
    ).catch(
      `Failed to download caches to ${ cache.cacheDir }`
    ).promise(
      this._trackProgress(cache, spinner).download()
    );
  }

  /**
   * @param {AbstractDriver} cache 
   * 
   * @returns {Promise}
   * 
   * @private
   */
  _uploadCache(cache) {
    const spinner = new Spinner(`Uploading caches to #${ cache.name }`);

    return spinner.then(
      `Caches uploaded from ${ cache.cacheDir }`
    ).catch(
      `Failed to upload caches from ${ cache.cacheDir }`
    ).promise(
      this._trackProgress(cache, spinner).upload()
    );
  }

  /**
   * @param {EventEmitter} emitter
   * @param {ora} spinner
   *
   * @returns {EventEmitter}
   *
   * @private
   */
  _trackProgress(emitter, spinner) {
    emitter.on(cacheEvents.cache.upload.progress, payload => {
      const { amount, total } = payload;

      spinner.prepend(`[${ prettyBytes(amount) }/${ prettyBytes(total) }]`);
    });

    spinner.prepend(`[0/0]`);

    return emitter;
  }
}

module.exports = CacheComponent;

'use strict';

const path = require('path');
const Container = require('../container');
const AbstractComponent = require('./abstract-component');

/**
 * Abstract configuration aware component
 */
class ConfigBasedComponent extends AbstractComponent {
  /**
   * @param {String} configPath
   */
  constructor(configPath = null) {
    super();

    this._container = null;
    this._readyPromise = Promise.resolve();
    this._configPath = configPath || `${ ConfigBasedComponent.MAIN_CONFIG_KEY }.${ this.name }`;
  }

  /**
   * @returns {*}
   */
  get container() {
    return this._container;
  }

  /**
   * @returns {string}
   */
  get configPath() {
    return this._configPath;
  }

  /**
   * @param {Emitter} emitter
   *
   * @returns {Promise}
   */
  subscribe(emitter) {
    this._readyPromise = this.waitConfig(emitter)
      .then(container => {
        this._container = container;
      });

    return Promise.resolve();
  }

  /**
   * @returns {Promise}
   */
  ready() {
    return this._readyPromise;
  }

  /**
   * @param {Emitter} emitter
   *
   * @returns {Promise}
   */
  waitConfig(emitter) {
    return new Promise((resolve, reject) => {
      emitter.on(this.events.config.load, (container, configFile) => {
        if (container.has(this.configPath)) {
          return this.prepareConfig(
            container.get(this.configPath, {}),
            configFile
          ).then(container => {
            this.setActive(true);

            resolve(container);
          }).catch(error => reject(error));
        }

        resolve(null);
      });
    });
  }

  /**
   * @param {*} config
   * @param {string} configFile
   *
   * @returns {Container}
   */
  prepareConfig(config, configFile) {
    const configFileRealPath = path.resolve(configFile);

    return Promise.resolve(this.createContainer(
      Object.assign({
        __file: configFileRealPath,
        __dir: path.dirname(configFileRealPath),
      }, config)
    ));
  }

  /**
   * @param {*} config
   *
   * @returns {Container}
   */
  createContainer(config) {
    return new Container(config);
  }

  /**
   * @returns {string}
   */
  static get MAIN_CONFIG_KEY() {
    return '$';
  }
}

module.exports = ConfigBasedComponent;

'use strict';

/**
 * Abstract coverage storage driver
 */
class AbstractDriver {
  /**
   * @param {string} name
   *
   * @returns {Promise}
   */
  read(name) {
    return this._read(name)
      .then(content => {
        if (!content) {
          return Promise.resolve(null);
        }

        return Promise.resolve(JSON.parse(content));
      });
  }

  /**
   * @param {string} name
   * @param {*} coverage
   *
   * @returns {Promise}
   */
  write(name, coverage) {
    return this._write(name, JSON.stringify(coverage));
  }

  /**
   * @param {string} name
   * 
   * @returns {Promise}
   *
   * @private
   */
  _read(name) {
    return Promise.reject(new Error(
      `${ this.constructor.name }._read(name) not implemented!`
    ));
  }

  /**
   * @param {string} name
   * @param {string} content
   * 
   * @returns {Promise}
   *
   * @private
   */
  _write(name, content) {
    return Promise.reject(new Error(
      `${ this.constructor.name }._write(name, content) not implemented!`
    ));
  }
}

module.exports = AbstractDriver;

'use strict';

const dot = require('dot-object');

/**
 * Coverage component events
 */
const events = {};

events.$ = [
  'coverage.report.create',
  'coverage.report.compare',
].map(eventPath => {
  dot.str(eventPath, eventPath, events);

  return eventPath;
});

module.exports = events;

'use strict';

const AbstractDriver = require('./abstract-driver');

/**
 * Coverage storage factory
 */
class Factory {  
  /**
   * @param {string} name
   * @param {*} args
   *
   * @returns {AbstractDriver}
   */
  static create(name, ...args) {
    const DriverImplementation = require(`./${ name }-driver`);
    const driver = new DriverImplementation(...args);

    if (!(driver instanceof AbstractDriver)) {
      throw new Error(`${ name } driver should be an implementation of AbstractDriver`);
    }

    return driver;
  }

  /**
   * @param {*} args
   *
   * @returns {VoidDriver}
   */
  static s3(...args) {
    return this.create('s3', ...args);
  }

  /**
   * @param {*} args
   *
   * @returns {VolatileDriver}
   */
  static volatile(...args) {
    return this.create('volatile', ...args);
  }
}

module.exports = Factory;

'use strict';

const path = require('path');
const AbstractDriver = require('./abstract-driver');
const AwsCredentials = require('../helper/aws-credentials');

/**
 * AWS S3 coverage storage driver
 */
class S3Driver extends AbstractDriver {
  /**
   * @param {string} path
   * @param {*} options
   * @param {boolean} includeNodeVersion
   */
  constructor(path, options, includeNodeVersion = true) {
    super();

    this._path = path;
    this._options = options;
    this._includeNodeVersion = includeNodeVersion;
    this._awsCredentials = new AwsCredentials(this.options);
    this._client = false;
  }

  /**
   * @returns {boolean}
   */
  get includeNodeVersion() {
    return this._includeNodeVersion;
  }

  /**
   * @returns {string}
   */
  get path() {
    return this._path;
  }

  /**
   * Get AWS.S3 client promise
   * @return {Promise}
   */
  get client() {
    if (this._client) {
      return Promise.resolve(this._client);
    }

    return this._awsCredentials.getConfig().then(AWS => Promise.resolve(new AWS.S3()));
  }

  /**
   * @returns {*}
   */
  get options() {
    return this._options;
  }

  /**
   * @param {string} name
   * @returns {Promise}
   * @private
   */
  _read(name) {
    const { Bucket, Key } = this._s3Payload(name);

    return this.client
      .then(S3 => S3.getObject({ Bucket, Key }).promise())
      .then(data => Promise.resolve(data.Body.toString()))
      .catch(error => {
        if (this._isMissingObject(error)) {
          return Promise.resolve(null);
        }

        return Promise.reject(error);
      });
  }

  /**
   * @param {string} name
   * @param {string} content
   * @returns {Promise}
   * @private
   */
  _write(name, content) {
    const Body = content;
    const { Bucket, Key } = this._s3Payload(name);

    return this.client
      .then(S3 => S3.upload({ Bucket, Key, Body }).promise())
      .then(data => Promise.resolve());
  }

  /**
   * @param {*} error
   *
   * @returns {boolean}
   * 
   * @private
   */
  _isMissingObject(error) {
    return [ 'NoSuchKey', 'NotFound' ].indexOf(error.code) !== -1;
  }

  /**
   * @param {string} name
   *
   * @returns {*}
   *
   * @private
   */
  _s3Payload(name) {
    const matches = this.path.match(
      /^(?:s3:\/\/|\/)?([^\/]+)(?:\/(.*))?$/i
    );

    if (matches.length === 2) {
      matches.push('');
    }

    const [ , Bucket, keyPrefix ] = matches;

    const Key = path.join(
      keyPrefix || '',
      this.includeNodeVersion ? process.version : '',
      name
    ).replace(/\\+/g, '/'); // ensure path delimiter set to slash

    return { Bucket, Key };
  }
}

module.exports = S3Driver;

'use strict';

const AbstractDriver = require('./abstract-driver');
const path = require('path');
const fse = require('fs-extra');
const os = require('os');
const fs = require('fs');
const pify = require('pify');

/**
 * Volatile coverage storage driver
 */
class VolatileDriver extends AbstractDriver {
  /**
   * @param {string} name
   * 
   * @returns {Promise}
   *
   * @private
   */
  _read(name) {
    const file = this._storageFile(name);

    return fse.pathExists(file)
      .then(exists => {
        if (!exists) {
          return Promise.resolve(null);
        }

        return pify(fs.readFile)(file);
      });
  }

  /**
   * @param {string} name
   * @param {string} content
   * 
   * @returns {Promise}
   *
   * @private
   */
  _write(name, content) {
    return fse.outputFile(this._storageFile(name), content);
  }

  /**
   * @param {string} name
   * 
   * @returns {string}
   *
   * @private
   */
  _storageFile(name) {
    return path.join(os.tmpDir(), '__recink_coverage_volatile__', name);
  }
}

module.exports = VolatileDriver;

'use strict';

/* eslint no-useless-call: 0 */

const DependencyBasedComponent = require('./dependency-based-component');
const istanbul = require('istanbul');
const testEvents = require('./test/events');
const events = require('./coverage/events');
const ContainerTransformer = require('./helper/container-transformer');
const path = require('path');
const fs = require('fs');
const pify = require('pify');
const StorageFactory = require('./coverage/factory');
const ModuleCompile = require('./helper/module-compile');

/**
 * Coverage component
 */
class CoverageComponent extends DependencyBasedComponent {
  /**
   * @param {*} args
   */
  constructor(...args) {
    super(...args);

    this._storage = null;
  }

  /**
   * @returns {string}
   */
  get name() {
    return 'coverage';
  }

  /**
   * @returns {string[]}
   */
  get dependencies() {
    return [ 'test' ];
  }

  /**
   * @returns {AbstractDriver}
   *
   * @private
   */
  get _comparatorStorage() {
    if (this._storage) {
      return this._storage;
    }

    const driver = this.container.get('compare.storage.driver', 'volatile');
    const options = this.container.get('compare.storage.options', []);

    this._storage = StorageFactory.create(driver, ...options);

    return this._storage;
  }

  /**
   * @param {istanbul.Collector} collector
   *
   * @returns {Promise}
   * 
   * @private
   */
  _doCompare(collector) {
    const allowedDelta = Math.abs(parseFloat(
      this.container.get('compare.negative-delta', 100)
    ));

    if (!allowedDelta || allowedDelta >= 100) {
      return Promise.resolve();
    }

    const storage = this._comparatorStorage;

    if (storage.constructor.name === 'VolatileDriver') {
      this.logger.debug(
        `Coverage stored in ${ storage._storageFile(CoverageComponent.COVERAGE_FILE) }`
      );
    }

    return storage.read(CoverageComponent.COVERAGE_FILE)
      .then(coverage => {
        const newCoverage = collector.getFinalCoverage();

        if (!coverage) {
          this.logger.info(
            this.logger.emoji.bicycle,
            'No previous coverage info saved...'
          );

          return storage.write(
            CoverageComponent.COVERAGE_FILE, 
            newCoverage
          );
        }

        const delta = this._calculateDelta(coverage, newCoverage);

        if (delta > allowedDelta) {          
          return Promise.reject(new Error(
            `Coverage delta decreased ${ delta.toFixed(2) } > ${ allowedDelta.toFixed(2) }`
          ));
        }

        this.logger.info(
          this.logger.emoji.bicycle,
          `Coverage delta checked ${ delta.toFixed(2) } >= ${ allowedDelta.toFixed(2) }`
        );

        return storage.write(
          CoverageComponent.COVERAGE_FILE, 
          newCoverage
        );
      });
  }

  /**
   * @param {*} coverage
   * @param {*} newCoverage
   *
   * @returns {number}
   *
   * @private
   */
  _calculateDelta(coverage, newCoverage) {
    const summary = this._summarizeCoverage(coverage);
    const newSummary = this._summarizeCoverage(newCoverage);

    return CoverageComponent.COVERAGE_KEYS
      .reduce((accumulator, key) => {
        return accumulator + 
          parseFloat(summary[key]) - 
          parseFloat((newSummary[key] || 0));
      }, 0) / CoverageComponent.COVERAGE_KEYS.length;
  }

  /**
   * @param {*} coverage
   *
   * @returns {*}
   *
   * @private
   */
  _summarizeCoverage(coverage) {
    const summary = {};

    const summaries = Object.keys(coverage)
      .map(file => {
        return istanbul.utils
          .summarizeFileCoverage(coverage[file]);
      });

    const summaryObj = istanbul.utils
      .mergeSummaryObjects
      .apply(null, summaries);

    Object.keys(summaryObj)
      .map(key => {
        summary[key] = summaryObj[key].pct;
      });

    return summary;
  }

  /**
   * @param {*} assetsToInstrument
   * @param {*} dispatchedAssets
   * @param {EmitModule} module
   *
   * @returns {Promise}
   * 
   * @private
   */
  _persistModuleBlankCoverage(assetsToInstrument, dispatchedAssets, module) {
    if (!assetsToInstrument[module.name]) {
      return Promise.resolve();
    }

    const coverageVariable = this._coverageVariable(module);

    return Promise.all(
      assetsToInstrument[module.name]
        .filter(asset => {
          return (dispatchedAssets[module.name] || []).indexOf(asset) === -1;
        })
        .map(asset => {
          return pify(fs.readFile)(asset)
            .then(content => {
              const instrumenter = new istanbul.Instrumenter({ coverageVariable });

              instrumenter.instrumentSync(
                content.toString(), 
                asset
              );

              global[coverageVariable] = global[coverageVariable] || {};
              global[coverageVariable][asset] = instrumenter.lastFileCoverage();

              return Promise.resolve();  
            });
        })
    );
  }

  /**
   * @param {Emitter} emitter
   * 
   * @returns {Promise}
   * '
   * @todo split into several abstractions
   */
  run(emitter) {
    return new Promise((resolve, reject) => {
      const collector = new istanbul.Collector();
      const reporter = new istanbul.Reporter();
      const reporters = this.container.get('reporters', {});
      const coverageVariables = [];
      const assetsToInstrument = {};
      const dispatchedAssets = {};

      Object.keys(reporters).map(reporterName => {
        const report = istanbul.Report.create(
          reporterName, 
          reporters[reporterName] || {}
        );

        reporter.reports[reporterName] = report;
      });

      emitter.onBlocking(testEvents.asset.test.skip, payload => {
        if (!this._match(payload.file)) {
          return Promise.resolve();
        }

        const { module, fileAbs } = payload;

        assetsToInstrument[module.name] = assetsToInstrument[module.name] || [];
        assetsToInstrument[module.name].push(fileAbs);

        return Promise.resolve();
      });

      emitter.onBlocking(testEvents.asset.tests.end, (mocha, module) => {
        return this._persistModuleBlankCoverage(
          assetsToInstrument,
          dispatchedAssets,
          module
        ).then(() => {
          delete assetsToInstrument[module.name];
          delete dispatchedAssets[module.name];

          return Promise.resolve();
        });
      });

      emitter.onBlocking(testEvents.asset.tests.start, (mocha, module) => {
        return new Promise(resolve => {
          const instrumenterCache = {};
          const coverageVariable = this._coverageVariable(module);
          const instrumenter = new istanbul.Instrumenter({ coverageVariable });

          coverageVariables.push(coverageVariable);

          if (mocha) {
            mocha.loadFiles = (fn => {
              const self = this;
              const moduleRoot = module.container.get('root');
              const coverableAssets = assetsToInstrument[module.name] || [];

              const preprocessor = (source, filename) => {
                if (coverableAssets.indexOf(filename) !== -1
                  && self._match(path.relative(moduleRoot, filename))) {

                  if (instrumenterCache.hasOwnProperty(filename)) {
                    return instrumenterCache[filename];
                  }

                  instrumenterCache[filename] = instrumenter.instrumentSync.call(instrumenter, source, filename);
                  dispatchedAssets[module.name] = dispatchedAssets[module.name] || [];
                  dispatchedAssets[module.name].push(filename);

                  return instrumenterCache[filename];
                }

                return source;
              };

              mocha.files.map(file => {
                file = path.resolve(file);

                mocha.suite.emit('pre-require', global, file, mocha);
                mocha.suite.emit(
                  'require',
                  ModuleCompile.require(file, {}, preprocessor),
                  file,
                  mocha
                );
                mocha.suite.emit('post-require', global, file, mocha);
              });

              fn && fn();
            });
          }

          resolve();
        });
      });

      emitter.onBlocking(testEvents.assets.test.end, () => {        
        coverageVariables.map(coverageVariable => {
          collector.add(global[coverageVariable] || {});
        });

        return emitter.emitBlocking(
          events.coverage.report.create, 
          istanbul, 
          reporter, 
          collector
        )
          .then(() => this._dumpCoverageStats(collector, reporter))
          .then(() => {
            return emitter.emitBlocking(
              events.coverage.report.compare, 
              istanbul, 
              reporter, 
              collector
            );
          })
          .then(() => this._doCompare(collector));
      });

      emitter.on(testEvents.assets.test.end, () => resolve());
    });
  }

  /**
   * @param {istanbul.Collector} collector
   * @param {istanbul.Reporter} reporter
   *
   * @returns {Promise}
   * 
   * @private
   */
  _dumpCoverageStats(collector, reporter) {
    return new Promise(resolve => {
      reporter.write(collector, false, () => {

        // @todo find a smarter way to indent the output (buffer it?)
        process.stdout.write('\n\n');

        resolve();
      });
    });
  }

  /**
   * @param {EmitModule} module
   *
   * @returns {string}
   *
   * @private
   */
  _coverageVariable(module) {
    const cleanModuleName = module.name.replace(/[^a-z0-9]/g, '_');

    return `__recink_coverage__${ cleanModuleName }__`;
  }

  /**
   * @param {string} file
   *
   * @returns {boolean}
   * 
   * @private
   */
  _match(file) {
    const pattern = this.container.get('pattern', []);
    const ignore = this.container.get('ignore', []);

    const result = pattern.filter(p => this._test(p, file)).length > 0
      && ignore.filter(i => this._test(i, file)).length <= 0;

    return result;
  }

  /**
   * @param {string|RegExp} pattern
   * @param {string} value
   *
   * @returns {boolean}
   *
   * @private
   */
  _test(pattern, value) {
    if (!(pattern instanceof RegExp)) {
      return value.indexOf(pattern.toString()) !== -1;
    }

    return pattern.test(value);
  }

  /**
   * @param {*} config
   * @param {string} configFile
   *
   * @returns {Container}
   */
  prepareConfig(config, configFile) {
    return super.prepareConfig(config, configFile)
      .then(container => {
        return (new ContainerTransformer(container))
          .addPattern('pattern')
          .addPattern('ignore')
          .transform();
      });
  }

  /**
   * @returns {Array}
   */
  static get COVERAGE_KEYS() {
    return [ 'lines', 'statements', 'functions', 'branches' ];
  }

  /**
   * @returns {string}
   */
  static get COVERAGE_FILE() {
    return 'coverage.json';
  }
}

module.exports = CoverageComponent;

'use strict';

const chalk = require('chalk');
const ConfigBasedComponent = require('./config-based-component');

/**
 * Abstract dependency aware component
 */
class DependencyBasedComponent extends ConfigBasedComponent {
  /**
   * @param {*} args
   */
  constructor(...args) {
    super(...args);
  }

  /**
   * @returns {string[]}
   */
  get dependencies() {
    return [];
  }

  /**
   * @param {Emitter} emitter
   *
   * @returns {Promise}
   */
  waitConfig(emitter) {
    return super.waitConfig(emitter).then(container => {
      if (container) {
        if (this.dependencies.length <= 0) {
          return Promise.resolve(container);
        }

        const waitComponents = this.dependencies.map(name => {
          const component = emitter.component(name);

          if (!component) {
            return Promise.resolve({ name, isActive: false });
          }

          return component.ready().then(() => Promise.resolve(component));
        });

        return Promise.all(waitComponents).then(components => {
          let failedComponents = [];

          components.map(component => {
            if (!component.isActive) {
              failedComponents.push(component.name);
            }
          });

          if (failedComponents.length > 0) {
            const failedInfo = failedComponents.map(c => chalk.red(c)).join(', ');

            this.setActive(false);
            this.logger.info(`${ this.logger.emoji.fire } ${ this.name } ~ ${ failedInfo }`);
          }

          return Promise.resolve(failedComponents.length <= 0 ? container : null);
        });
      }

      return Promise.resolve(null);
    });
  }
}

module.exports = DependencyBasedComponent;

'use strict';

const createTestCafe = require('testcafe');
const registerBrowsers = require('./testcafe/browsers-registrar');

/**
 * End2End test runner (build on top of TestCafe)
 * @see https://devexpress.github.io/testcafe/documentation/using-testcafe/common-concepts/browser-support.html
 * @see https://devexpress.github.io/testcafe/documentation/using-testcafe/common-concepts/reporters.html
 */
class E2ERunner {
  /**
   * @param {String} hostName
   * @param {Array} hostPorts
   */
  constructor(options = {}) {
    const defaults = {
      hostPorts: E2ERunner.DEFAULT_SERVER_PORTS,
      hostName: E2ERunner.DEFAULT_SERVER_HOSTNAME,
      reporter: E2ERunner.DEFAULT_REPORTER,
      browsers: E2ERunner.DEFAULT_BROWSERS,
      screenshotsPath: process.cwd(),
      takeOnFail: false,
    };

    this._config = Object.assign({}, defaults, options);

    console.log('this._config', JSON.stringify(this._config, null, 2));
  }

  /**
   * Init testcafe runner
   * @returns {Promise}
   */
  getTestCafe() {
    if (this._testcafe) {
      return Promise.resolve();
    }

    return createTestCafe(this._config.hostName, ...this._config.hostPorts).then(testcafe => {
      registerBrowsers();
      this._testcafe = testcafe;

      return Promise.resolve();
    });
  }

  /**
   * Run tests
   * @param {Array|String} tests
   * @param {Object} options
   * @returns {Promise}
   */
  run(tests) {
    return this.getTestCafe().then(() => {
      const runner = this._testcafe.createRunner().screenshots(this._config.screenshotsPath, this._config.takeOnFail);

      return runner
        .src(tests)
        .browsers(this._config.browsers)
        .reporter(this._config.reporter)
        .run(E2ERunner.RUN_OPTIONS)
        .then(failed => {
          if (failed > 0) {
            return Promise.reject(failed);
          }

          return Promise.resolve();
        });
    });
  }

  /**
   * Terminate
   * @returns {Promise}
   */
  cleanup() {
    return this._testcafe.close();
  }

  /**
   * @returns {String}
   */
  static get DEFAULT_SERVER_HOSTNAME() {
    return 'localhost';
  }

  /**
   * @returns {Number[]}
   */
  static get DEFAULT_SERVER_PORTS() {
    return [ 1337, 1338 ];
  }

  /**
   * @todo figure out options that really matters!
   * @ex: skipJsErrors: true, quarantineMode: true
   * @returns {*}
   */
  static get RUN_OPTIONS() {
    return {};
  }

  /**
   * @returns {String}
   */
  static get DEFAULT_REPORTER() {
    return 'spec';
  }

  /**
   * @returns {String[]}
   */
  static get DEFAULT_BROWSERS() {
    return [ 'puppeteer' ];
  }
}

module.exports = E2ERunner;

