'use strict';

const AWS = require('aws-sdk');

/**
 * AWS security credentials
 */
class AwsCredentials {
  /**
   * @param {Object} options
   */
  constructor(options) {
    this._options = options || {};
    this._providers = [
      new AWS.EnvironmentCredentials(
        options.hasOwnProperty('envPrefix') ? options.envPrefix : 'AWS'
      ),
      new AWS.SharedIniFileCredentials({
        profile: options.hasOwnProperty('profile') ? options.profile : 'default'
      }),
      AWS.ECSCredentials.prototype.isConfiguredForEcsCredentials()
        ? new AWS.ECSCredentials() : new AWS.EC2MetadataCredentials()
    ];

    if (options.hasOwnProperty('accessKeyId') && options.hasOwnProperty('secretAccessKey')) {
      this._providers.push(
        new AWS.Credentials({ accessKeyId: options.accessKeyId, secretAccessKey: options.secretAccessKey})
      );
    }
  }

  /**
   * Get AWS Configuration
   * @return {Promise}
   */
  getConfig() {
    return new AWS.CredentialProviderChain(this._providers).resolvePromise().then(credentials => {
      let roleArn = this._getRoleArn();

      AWS.config.credentials = roleArn
        ? new AWS.TemporaryCredentials({ RoleArn: roleArn }, credentials)
        : credentials;

      if (this._options.hasOwnProperty('region')) {
        AWS.config.update({ region: this._options.region });
      }

      return Promise.resolve(AWS);
    });
  }

  /**
   * Compose and validate Role ARN
   * @return {boolean}
   * @private
   */
  _getRoleArn() {
    let roleArn = this._options.roleArn;

    if (this._options.hasOwnProperty('accountId') && this._options.hasOwnProperty('roleName')) {
      roleArn = `arn:aws:iam::${this._options.accountId}:role/${this._options.roleName}`
    }

    return /^arn:aws:iam::[0-9]{12}:role\/[a-zA-Z0-9+=,.@\-_]*$/.test(roleArn) ? roleArn : false;
  }
}

module.exports = AwsCredentials;

'use strict';

const Transformer = require('./transformer');
const patternTransformer = require('./pattern-transformer');

/**
 * Apply transformations to an configuration container
 */
class ContainerTransformer {
  /**
   * @param {Container} container
   */
  constructor(container) {
    this._container = container;
    this._transformers = [];
  }

  /**
   * @param {string} path
   *
   * @returns {ContainerTransformer}
   */
  addPattern(path) {
    return this.add({ path, transformer: patternTransformer });
  }

  /**
   * @param {string} path
   * @param {function} transformer
   *
   * @returns {Transformer}
   */
  create(path, transformer) {
    return new Transformer(path, transformer);
  }

  /**
   * @param {Transformer} transformerInstance
   *
   * @returns {ContainerTransformer}
   */
  add(transformerInstance) {
    if (!(transformerInstance instanceof Transformer)) {
      const { path, transformer } = transformerInstance;

      transformerInstance = this.create(path, transformer);
    }

    this._transformers.push(transformerInstance);

    return this;
  }

  /**
   * @returns {Transformer[]}
   */
  get transformers() {
    return this._transformers;
  }

  /**
   * @returns {Promise}
   */
  transform() {
    return Promise.all(this.transformers.map(transformer => {
      if (this.container.has(transformer.path)) {
        return transformer.transform(this.container.get(transformer.path))
          .then(value => {
            this.container.set(transformer.path, value);

            return Promise.resolve();
          });
      }

      return Promise.resolve();
    })).then(() => Promise.resolve(this.container));
  }

  /**
   * @returns {Container}
   */
  get container() {
    return this._container;
  }
}

module.exports = ContainerTransformer;

