'use strict';

const AbstractConfig = require('./abstract-config');
const yamlReader = require('yaml-js');
const yamlWriter = require('yamljs');

/**
 * YAML configuration manager
 */
class YamlConfig extends AbstractConfig {
  /**
   * @param {string} rawConfig
   *
   * @returns {Promise}
   */
  decode(rawConfig) {
    try {
      return Promise.resolve(yamlReader.load(rawConfig));
    } catch (error) {
      return Promise.reject(error);
    }
  }

  /**
   * @param {*} config
   *
   * @returns {Promise}
   */
  encode(config) {
    try {
      return Promise.resolve(yamlWriter.stringify(
        config, 
        YamlConfig.INLINE_DEPTH, 
        YamlConfig.INDENTATION
      ));
    } catch (error) {
      return Promise.reject(error);
    }
  }

  /**
   * @returns {number}
   */
  static get INLINE_DEPTH() {
    return 8;
  }

  /**
   * @returns {number}
   */
  static get INDENTATION() {
    return 2;
  }
}

module.exports = YamlConfig;

'use strict';

const print = require('print');
const dot = require('dot-object');
dot.overwrite = true;

/**
 * Configuration container
 */
class Container {
  /**
   * @param {*} config
   */
  constructor(config = {}) {
    this._config = config;
  }

  /**
   * @param {*} config
   *
   * @returns {Container}
   */
  reload(config) {
    this._config = config;

    return this;
  }

  /**
   * @returns {Array}
   */
  listKeys() {
    return Object.keys(this._config);
  }

  /**
   * @param {string} path
   * @param {*} value
   *
   * @returns {Container}
   */
  set(path, value) {
    dot.str(path, value, this._config);

    return this;
  }

  /**
   * @param {string} path
   * @param {*} defaultValue
   *
   * @returns {*}
   */
  get(path, defaultValue = null) {
    if (!this.has(path)) {
      return defaultValue;
    }

    return dot.pick(path, this._config);
  }

  /**
   * @param {string} path
   *
   * @returns {boolean}
   */
  has(path) {
    return typeof dot.pick(path, this._config) !== 'undefined';
  }

  /**
   * Remove the value
   * @param {String} path
   * @return {Container}
   */
  del(path) {
    dot.del(path, this._config);

    return this;
  }

  /**
   * @returns {string}
   */
  dump() {
    return print(this.raw, {
      showArrayIndices: true,
      showArrayLength: true,
      sortProps: false,
    }).replace(/\t/g, '   ');
  }

  /**
   * @returns {*}
   */
  get raw() {
    return this._config;
  }
}

module.exports = Container;

'use strict';

const EventEmitter = require('events');

/**
 * Extended Emitter implementation
 */
class Emitter extends EventEmitter {
  /**
   * @param {*} args
   */
  constructor(...args) {
    super(...args);

    this._blockingListeners = {};
    this._processing = {};
    this._parallel = {};
  }

  /**
   * @param {string} event
   * @param {number} count
   *
   * @returns {Emitter}
   */
  maxParallel(event, count) {
    this._parallel[event] = count;

    return this;
  }

  /**
   * @param {string} event
   * @param {*} args
   *
   * @returns {Promise}
   */
  emitBlocking(event, ...args) {
    return this._waitAllowParallel(event)
      .then(() => this._dispatch(event, args))
      .then(() => {
        this
          ._cleanupListeners(event)
          ._removeParallel(event)
          .emit(event, ...args);

        return Promise.resolve();
      })
      .catch(error => {
        this
          ._cleanupListeners(event)
          ._removeParallel(event);

        return Promise.reject(error);
      });
  }

  /**
   * @param {string} event
   * @param {function} listener
   * @param {number} priority
   *
   * @returns {Emitter}
   */
  onBlocking(event, listener, priority = Emitter.DEFAULT_PRIORITY) {
    return this._pushListener(event, listener, priority, 'on');
  }

  /**
   * @param {string} event
   * @param {function} listener
   * @param {number} priority
   *
   * @returns {Emitter}
   */
  onceBlocking(event, listener, priority = Emitter.DEFAULT_PRIORITY) {
    return this._pushListener(event, listener, priority, 'once');
  }

  /**
   * @param {string} event
   *
   * @returns {Emitter}
   *
   * @private
   */
  _removeParallel(event) {
    if (!this._processing.hasOwnProperty(event)) {
      return this;
    }

    this._processing[event]--;

    return this;
  }

  /**
   * @param {string} event
   *
   * @returns {Emitter}
   *
   * @private
   */
  _addParallel(event) {
    this._processing[event] = this._processing[event] || 0;
    this._processing[event]++;

    return this;
  }

  /**
   * @param {string} event
   *
   * @returns {boolean}
   *
   * @private
   */
  _allowParallel(event) {
    return !this._parallel.hasOwnProperty(event)
      || !this._processing.hasOwnProperty(event)
      || this._processing[event] < this._parallel[event];
  }

  /**
   * @param {string} event
   * @param {number} interval
   *
   * @returns {Promise}
   *
   * @private
   */
  _waitAllowParallel(event, interval = 100) {
    if (this._allowParallel(event)) {
      this._addParallel(event);
      return Promise.resolve();
    }

    return new Promise(resolve => {
      const id = setInterval(() => {
        if (this._allowParallel(event)) {
          this._addParallel(event);
          clearInterval(id);
          process.nextTick(() => resolve());
        }
      }, interval);
    });
  }

  /**
   * @param {string} event
   * @param {function} listener
   * @param {number} priority
   * @param {string} method
   *
   * @returns {Emitter}
   *
   * @private
   */
  _pushListener(event, listener, priority, method) {
    this._blockingListeners[event] = this._blockingListeners[event] || [];
    this._blockingListeners[event].push({ listener, priority, method });
    this._blockingListeners[event].sort((a, b) => {
      return b.priority - a.priority;
    });

    return this;
  }

  /**
   * @param {string} event
   *
   * @returns {Emitter}
   * 
   * @private
   */
  _cleanupListeners(event) {
    if (!this._blockingListeners.hasOwnProperty(event) ) {
      return this;
    }

    this._blockingListeners[event] = this._blockingListeners[event].filter(l => !!l);

    return this;
  }

  /**
   * @param {string} event
   * @param {*} args
   * @param {number} _i
   * 
   * @returns {Promise}
   * 
   * @private
   */
  _dispatch(event, args, _i = 0) {    
    if (!this._blockingListeners.hasOwnProperty(event) 
      || this._blockingListeners[event].length < _i + 1) {
      return Promise.resolve();
    }

    const dispatcher = this._blockingListeners[event][_i];
    const result = dispatcher.listener(...args);

    if (dispatcher.method === 'once') {
      delete this._blockingListeners[event][_i];
    }

    if (!result || typeof result !== 'object' || !(result instanceof Promise)) {
      return this._dispatch(event, args, _i + 1);
    }

    return result.then(() => this._dispatch(event, args, _i + 1));
  }

  /**
   * @returns {number}
   */
  static get DEFAULT_PRIORITY() {
    return 0;
  }
}

module.exports = Emitter;

'use strict';

const dot = require('dot-object');

/**
 * REciNK main events
 */
const events = {};

events.$ = [
  'config.preprocess',
  'config.load',
  'components.load',
  'components.run',
  'component.load',
  'component.init',
  'component.run',
  'component.teardown',
  'component.ready',
  'component.subscribe',
].map(eventPath => {
  dot.str(eventPath, eventPath, events);

  return eventPath;
});

module.exports = events;

'use strict';

/**
 * Environment helpers
 */
class Env {
  /**
   * @returns {boolean}
   */
  static get isCI() {
    return Env.isTravis;
  }

  /**
   * @returns {boolean}
   */
  static get isTravis() {
    const isCI = !!(Env.read('CI', false) || Env.read('CONTINUOUS_INTEGRATION', false));

    return !!(isCI && Env.read('TRAVIS', false));
  }

  /**
   * @returns {boolean}
   */
  static get isGlobalInstallation() {
    return !!Env.read('npm_config_global', false);
  }

  /**
   * @param {string} name
   * 
   * @returns {*}
   */
  static exists(name) {
    return process.env.hasOwnProperty(name);
  }

  /**
   * @param {string} name
   * @param {*} defaultValue
   * 
   * @returns {*}
   */
  static read(name, defaultValue = null) {
    if (!Env.exists(name)) {
      return defaultValue;
    }

    return process.env[name];
  }
}

module.exports = Env;

'use strict';

const fs = require('fs');
const path = require('path');

/**
 * Read directory recursively
 * @param {String} dir
 * @param {String} filter
 * @param {Function} callback
 */
function walkDir(dir, filter, callback) {
  if (!fs.existsSync(dir)) {
    return;
  }

  let files = fs.readdirSync(dir);

  for (let i = 0; i < files.length; i++) {
    let filename = path.join(dir, files[i]);
    let stat = fs.lstatSync(filename);

    if (stat.isDirectory()) {
      walkDir(filename, filter, callback);
    } else if (filter.test(filename)) {
      callback(filename);
    }
  }
}

exports.walkDir = walkDir;

/**
 * Get filenames from dir by RegExp pattern
 * @param {String} dir
 * @param {RegExp} regExp
 * @return {Array}
 */
function getFilesByPattern(dir, regExp) {
  let fileNames = [];
  walkDir(dir, regExp, (fileName) => fileNames.push(fileName));

  return fileNames;
}

exports.getFilesByPattern = getFilesByPattern;

/**
 * Compares two software version numbers (e.g. "1.7.1" or "1.2b").
 *
 * @param {String} v1 The first version to be compared.
 * @param {String} v2 The second version to be compared.
 * @param {Object} [options] Optional flags that affect comparison behavior:
 *
 * lexicographical: true | compares each part of the version strings lexicographically
 * instead of naturally; this allows suffixes such as "b" or "dev" but will cause "1.10"
 * to be considered smaller than "1.2".
 *
 * zeroExtend: true | changes the result if one version string has less parts than the other.
 * In this case the shorter string will be padded with "zero" parts instead of being considered smaller.
 *
 * @returns {Number|NaN} 0 / equals; -1 / smaller; +1 / higher; NaN / wrong format
 */
function versionCompare(v1, v2, options = {}) {
  let lexicographical = options && options.lexicographical,
    zeroExtend = options && options.zeroExtend,
    v1parts = v1.split('.'),
    v2parts = v2.split('.');

  /**
   * @param {String} x
   * @returns {Boolean}
   */
  function isValidPart(x) {
    return (lexicographical ? /^\d+[A-Za-z]*$/ : /^\d+$/).test(x);
  }

  if (!v1parts.every(isValidPart) || !v2parts.every(isValidPart)) {
    return NaN;
  }

  if (zeroExtend) {
    while (v1parts.length < v2parts.length) {
      v1parts.push('0');
    }

    while (v2parts.length < v1parts.length) {
      v2parts.push('0');
    }
  }

  if (!lexicographical) {
    v1parts = v1parts.map(Number);
    v2parts = v2parts.map(Number);
  }

  for (let i = 0; i < v1parts.length; ++i) {
    if (v2parts.length === i) {
      return 1;
    }

    if (v1parts[i] === v2parts[i]) {
      continue;
    } else if (v1parts[i] > v2parts[i]) {
      return 1;
    } else {
      return -1;
    }
  }

  if (v1parts.length !== v2parts.length) {
    return -1;
  }

  return 0;
}

exports.versionCompare = versionCompare;

'use strict';

const chalk = require('chalk');

/**
 * Logger abstraction
 */
class Logger {
  /**
   * @returns {chalk} chalk
   */
  static get chalk() {
    return chalk;
  }

  /**
   * @param {*} logger
   *
   * @returns {Logger}
   */
  static customLogger(logger) {
    this._customLogger = logger;

    return this;
  }

  /**
   * @param {number} level
   *
   * @returns {Logger}
   */
  static level(level) {
    this._dbgLevel = level;

    return this;
  }

  /**
   * @param {number} level
   *
   * @returns {Logger}
   */
  static addLevel(level) {
    this._dbgLevel = this._level | level;

    return this;
  }

  /**
   * @param {*} args
   * @returns {Logger}
   */
  static debug(...args) {
    return this._exec('debug', ...args);
  }

  /**
   * @param {*} args
   * @returns {Logger}
   */
  static log(...args) {
    return this._exec('log', ...args);
  }

  /**
   * @param {*} args
   * @returns {Logger}
   */
  static info(...args) {
    return this._exec('info', ...args);
  }

  /**
   * @param {*} args
   * 
   * @returns {Logger}
   */
  static warn(...args) {
    return this._exec('warn', ...args);
  }

  /**
   * @param {*} args
   * 
   * @returns {Logger}
   */
  static error(...args) {
    return this._exec('error', ...args);
  }

  /**
   * @returns {*}
   *
   * @private
   */
  static get _logger() {
    return this._customLogger || console;
  }

  /**
   * @returns {number}
   *
   * @private
   */
  static get _level() {
    return this._dbgLevel || (this.WARN | this.ERROR);
  }

  /**
   * @param {string} method
   * @param {*} args
   *
   * @returns {Logger}
   * 
   * @private
   */
  static _exec(method, ...args) {
    if (this._logger instanceof console.constructor) {
      if (this._level & this[method.toUpperCase()]) {
        if (method === 'debug') {
          method = 'log';
        }

        this._logger[method](
          this._color(method).open,
          ...args,
          this._color(method).close
        );
      }
    } else { // rely on winston compatible logger
      this._logger[method](...args);
    }

    return this;
  }

  /**
   * @param {string} method
   * 
   * @returns {string}
   *
   * @private
   */
  static _color(method) {
    let color;

    switch (method) {
      case 'info':
        color = 'green';
        break;  
      case 'warn':
        color = 'yellow';
        break;   
      case 'error':
        color = 'red';
        break;    
      case 'log':
      default:
        color = 'white';
        break;
    }

    return chalk.styles[color];
  }

  /**
   * @returns {*}
   */
  static get emoji() {
    return {
      empty: '',
      rocket: '🚀 ',
      cross: '❌ ',
      check: '✅ ',
      banana: '🍌 ',
      smiley: '😃 ',
      fire: '🔥 ',
      diamond: '💎 ',
      gift: '🎁 ',
      poop: '💩 ',
      magic: '✨ ',
      hat: '🎩 ',
      new: '🆕 ',
      fist: '👊 ',
      beer: '🍺 ',
      bicycle: '🚲 ',
      star: '⭐️ ',
      moon_full: '🌔 ',
      moon_half: '🌓 ',
      moon_empty: '🌘 ',
    };
  }

  /**
   * @returns {number}
   */
  static get ALL() {
    return this.LOG | this.DEBUG | this.INFO | this.WARN | this.ERROR;
  }

  /**
   * @returns {number}
   */
  static get LOG() {
    return 1;
  }

  /**
   * @returns {number}
   */
  static get DEBUG() {
    return 2;
  }

  /**
   * @returns {number}
   */
  static get INFO() {
    return 4;
  }

  /**
   * @returns {number}
   */
  static get WARN() {
    return 8;
  }

  /**
   * @returns {number}
   */
  static get ERROR() {
    return 16;
  }
}

module.exports = Logger;

'use strict';

const path = require('path');
const merge = require('merge');
const Emitter = require('./emitter');
const events = require('./events');
const logger = require('./logger');
const Container = require('./container');
const configFactory = require('./config/factory');
const AbstractComponent = require('./component/abstract-component');

/**
 * Recink entry point
 */
class Recink extends Emitter {
  constructor() {
    super();

    this._config = {};
    this._components = [];
    this._container = new Container();
    this._registerDebugers();
  }

  /**
   * @private
   */
  _registerDebugers() {
    this.on(events.config.load, (container, configFile) => {
      logger.info(logger.emoji.smiley, `Load config from ${ configFile }`);
      logger.debug(container.dump());
    });

    this.on(events.components.load, (...components) => {
      logger.debug(
        'Loading components -', components.map(c => c.name).join(', ') || 'None'
      );
    });

    this.on(events.component.load, component => {
      logger.debug(`Load component ${ component.name }`);
    });

    this.on(events.component.subscribe, component => {
      logger.debug(`Component ${ component.name } is subscribed`);
    });

    this.on(events.component.ready, component => {
      logger.debug(`Component ${ component.name } is ready`);
    });

    this.on(events.components.run, (...components) => {
      components.map(component => {
        logger.info(
          component.isActive ? logger.emoji.check : logger.emoji.cross,
          `${ component.name.toUpperCase() } component`
        );
      });
    });
  }

  /**
   * @returns {Promise}
   */
  run() {
    this.emit(events.components.run, ...this._components);

    const activeComponents = this._components.filter(component => component.isActive);

    if (activeComponents.length <= 0) {
      return Promise.resolve();
    }

    return Promise.all(activeComponents.map(component => {
      this.emit(events.component.init, component);

      return component.init(this);
    }))
      .then(() => {
        return Promise.all(activeComponents.map(component => {
          this.emit(events.component.run, component);

          return component.run(this);
        }));
      })
      .then(() => {
        return Promise.all(this._components.map(component => {
          this.emit(events.component.teardown, component);

          return component.teardown(this);
        }));
      });
  }

  /**
   * @param {Function} targetClass
   * @returns {string|null}
   * @private
   */
  _getBaseClass(targetClass) {
    if (targetClass instanceof Function) {
      let baseClass = targetClass;

      while (baseClass) {
        const newBaseClass = Object.getPrototypeOf(baseClass);

        if (newBaseClass && newBaseClass !== Object && newBaseClass.name) {
          baseClass = newBaseClass;
        } else {
          break;
        }
      }

      return baseClass.name;
    }

    return null;
  }

  /**
   * @param {AbstractComponent} components
   * @returns {Promise}
   */
  components(...components) {
    this.emit(events.components.load, ...components);

    if (components.length <= 0) {
      return Promise.resolve();
    }

    return Promise.all(components.map(component => {
      if (!(component instanceof AbstractComponent)
        && [ 'ConfigBasedComponent', 'AbstractComponent' ]
          .indexOf(this._getBaseClass(component.constructor)) === -1) {

        return Promise.reject(new Error(
          `Component ${ component.constructor.name } should be an instance of AbstractComponent`
        ));
      }

      component.setLogger(logger);
      this._components.push(component);
      this.emit(events.component.load, component);

      return component.subscribe(this)
        .then(() => {
          return this.emit(events.component.subscribe, component, this);
        })
        .then(() => component.ready())
        .then(() => {
          this.emit(events.component.ready, component);
          return Promise.resolve(component);
        });
    }));
  }

  /**
   * @param {string} configFile
   * @param {*} extendConfigs
   * @returns {Promise}
   */
  configureExtend(configFile, ...extendConfigs) {
    if (extendConfigs.length <= 0) {
      return this.configure(configFile);
    }

    const promises = extendConfigs
      .concat([ configFile ])
      .map(cfgFile => configFactory.guess(cfgFile).load());

    return Promise.all(promises).then(configVectors => {
      return Promise.resolve(merge.recursive(true, ...configVectors));
    });
  }

  /**
   * @param {string} configFile
   * @returns {Promise}
   */
  configure(configFile = Recink.CONFIG_FILE) {
    return configFactory.guess(configFile).load().then(config => Promise.resolve(config));
  }

  /**
   * @param {Object} config
   * @param {String} configFile
   * @returns {Promise}
   */
  configLoad(config, configFile) {
    return this.emitBlocking(events.config.preprocess, config).then(() => {
      this._config = config;
      this._container.reload(this._config);
      this.emit(events.config.load, this.container, configFile);

      return Promise.resolve(this._config);
    });
  }

  /**
   * @param {string} name
   *
   * @returns {AbstractComponent}
   */
  component(name) {
    return this._components.filter(c => c.name === name)[0] || null;
  }

  /**
   * @returns {AbstractComponent[]}
   */
  listComponents() {
    return this._components;
  }

  /**
   * @returns {Container}
   */
  get container() {
    return this._container;
  }

  /**
   * @returns {AbstractConfig}
   */
  get config() {
    return this._config;
  }

  /**
   * @returns {string}
   */
  static get CONFIG_FILE() {
    return path.resolve(process.cwd(), this.CONFIG_FILE_NAME);
  }

  /**
   * @returns {string}
   */
  static get CONFIG_FILE_NAME() {
    return '.recink.yml';
  }
}

module.exports = Recink;
